ForEach.Job:
./mkdirs.sh ${disk}/${pass}
mv -f ${disk}/${pass}/logs/${pre}${run}.${fnum}_recon_${rel}.err ${disk}/${passfail}/logs>& /dev/null mv -f ${disk}/${pass}/logs/${pre}${run}.${fnum}_recon_${rel}.out ${disk}/${passfail}/logs>& /dev/null mv -f ${disk}/${pass}/recon/${pre}${run}.${fnum}_recon_${rel}.slcio ${disk}/${passfail}/recon>& /dev/null mv -f ${disk}/${pass}/dst/${pre}${run}.${fnum}_dst_${rel}.root ${disk}/${passfail}/dst>& /dev/null mv -f ${disk}/${pass}/data_quality/dqm/${pre}${run}.${fnum}_dqm_${rel}.root ${disk}/${passfail}/data_quality/dqm>& /dev/null mv -f ${disk}/${pass}/data_quality/recon/${pre}${run}.${fnum}_recon_${rel}.txt ${disk}/${passfail}/data_quality/recon>& /dev/null
mv -f ${disk}/${pass}/skim/pulser/${pre}${run}.${fnum}_pulser_${rel}.slcio ${disk}/${passfail}/skim/pulser>& /dev/null mv -f ${disk}/${pass}/skim/moller/${pre}${run}.${fnum}_moller_${rel}.slcio ${disk}/${passfail}/skim/moller>& /dev/null mv -f ${disk}/${pass}/skim/fee/${pre}${run}.${fnum}_fee_${rel}.slcio ${disk}/${passfail}/skim/fee>& /dev/null mv -f ${disk}/${pass}/skim/v0/${pre}${run}.${fnum}_v0_${rel}.slcio ${disk}/${passfail}/skim/v0>& /dev/null
mv -f ${disk}/${pass}/skim/dst/pulser/${pre}${run}.${fnum}_pulser_${rel}.root ${disk}/${passfail}/skim/dst/pulser>& /dev/null mv -f ${disk}/${pass}/skim/dst/moller/${pre}${run}.${fnum}_moller_${rel}.root ${disk}/${passfail}/skim/dst/moller>& /dev/null mv -f ${disk}/${pass}/skim/dst/fee/${pre}${run}.${fnum}_fee_${rel}.root ${disk}/${passfail}/skim/dst/fee>& /dev/null mv -f ${disk}/${pass}/skim/dst/v0/${pre}${run}.${fnum}_v0_${rel}.root ${disk}/${passfail}/skim/dst/v0>& /dev/null
mkdir ./fieldmap ; mv ${fieldmap} fieldmap
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running RECON ..." ${java} ${jrecopts} -cp ${jar} org.hps.evio.EvioToLcio -x ${recon_steer} -r -d ${detector} -R ${run} -DoutputFile=out in.evio ${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "Done RECON."
if ( ! -f ./out.slcio ) then echo ; echo ######################### Missing out.slcio ################################ exit endif if ( `ls -l out.slcio | awk '{print$5}'` == "0" ) then echo ; echo ########################### Zero out.slcio ################################# exit endif
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running DQM #1 ..." ${java} -jar ${jar} -r ${dq_steer} -i out.slcio > dq.txt
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running DQM #2 ..." ${java} -Xmx2000m -jar ${jar} -r ${dqm_steer} -DoutputFile=dqm -i out.slcio
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running DST ..." ${dstmaker} out.slcio -o dst.root ${dstopts}
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running Pulser Skim ..." ${java} -jar ${jar} -r ${pulser_steer} -DoutputFile=pulser -i out.slcio ${dstmaker} pulser.slcio -o pulser.root ${dstopts}
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running Moller Skim ..." ${java} -jar ${jar} -r ${moller_steer} -DoutputFile=moller -i out.slcio ${dstmaker} moller.slcio -o moller.root ${dstopts}
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running FEE Skim ..." ${java} -jar ${jar} -r ${fee_steer} -DoutputFile=fee -i out.slcio ${dstmaker} fee.slcio -o fee.root ${dstopts}
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running V0 Skim ..." ${java} -jar ${jar} -r ${v0_steer} -DoutputFile=v0 -i out.slcio ${dstmaker} v0.slcio -o v0.root ${dstopts}
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running P0 Skim ..." ${java} -jar ${jar} -r ${p0_steer} -DoutputFile=p0 -i out.slcio ${dstmaker} p0.slcio -o p0.root ${dstopts}
${augid} ; ${comm} ; ${comm} ; ${datls} ; echo "#### Running S0 Skim ..." ${java} -jar ${jar} -r ${s0_steer} -DoutputFile=s0 -i out.slcio ${dstmaker} s0.slcio -o s0.root ${dstopts}
CANON CANON
